import React , {useState , useEffect} from 'react'
import edit_btn from '../icons/edit_btn.svg'
import adding from '../icons/adding.svg'
import x_marque from '../icons/x_marque.svg'
import axios from 'axios'

function Home() {
  const [data , setDate] = useState()
  useEffect(()=>{
    getData();
  },[])    
  let getData = async () => {

    let respons = await fetch (`http://127.0.0.1:8000/project`)
    let data = await respons.json()
    setDate(data)

  }
  const [show , setShow ] = useState(false)
  const [project_name , setproject_name ] = useState('')


  const handleSumbit = async ()=> {
    const DataForm= new FormData();
    DataForm.append('name',project_name)
   
    

    await axios ({
      method : 'post' , 
      url : 'http://127.0.0.1:8000/project' ,
      data :  DataForm
  })
  .then((response)=>{
      console.log(response.data) ;
  }) .catch(function (error) {
      console.log(error)
    });
    setproject_name('')
    setShow(false)
  }

  console.log(data)

  return (
    <div className='Home'>
      {
        show ? 
            <div className='form_container center'>
                <div className='form center'>
                  <img className='x_marque' src={x_marque} onClick={()=>setShow(false)} />
                    <div className='box'>
                      <p>Project name </p>
                      <input onChange={(e)=>setproject_name(e.target.value)} value={project_name}  />
                    </div>
                    {/* <div className='box'>
                      <p>Dead Line  </p>
                      <input type='date' />
                    </div> */}
                    <button onClick={()=>handleSumbit()} >Save</button>
                </div>
            </div>
            : ''
      }
       <div className='add_task center' onClick={()=>setShow(true)}>
            <img src={adding}  /> Add project  
        </div>
        {
          data?.map((ob,i)=>{
            
          })
        }
        <div className='project spacearound'>
          <h3>projects name</h3>
          <p>60% <span>6/10</span></p>
          <p>7 days</p>
          <img src={edit_btn} />

        </div>


      
        
    </div>
  )
}

export default Home
